section.programlisting:
#include <assert.h>
#include <boost/char_type_switch/cstring.hpp>

int main(void)
{
	using namespace ::boost::cts;
	bchar aaa[4];
	bchar bbb[4];

	bstrcpy(aaa, BOOST_CTS_LIT("foo"));
	bstrcpy(bbb, BOOST_CTS_LIT("bar"));
	assert(bstrcmp(aaa, bbb) != 0);

	bstrcpy(bbb, aaa);
	assert(bstrcmp(aaa, bbb) == 0);
	return 0;
}
