typedef ::std::pair< int, ::std::string> P;
BOOST_MPL_ASSERT((
	iterator_not_equal<
		begin< BOOST_MIRRORED_CLASS(P)::attributes >::type,
		end< BOOST_MIRRORED_CLASS(P)::attributes >::type
	>
));
BOOST_MPL_ASSERT((
	iterator_not_equal<
		end< BOOST_MIRRORED_CLASS(P)::all_attributes >::type,
		point_to< 
			BOOST_MIRRORED_CLASS(P)::all_attributes, 
			mpl::int_<1>
		>::type
	>
));
