description.programlisting:
// note that the registering must be done
// in the boost::mirror namespace

namespace boost {
namespace mirror {

BOOST_MIRROR_REG_TYPEDEF_GLOBAL_SCOPE(foo)
// or use the shorter version
BOOST_MIRROR_REG_TYPEDEF_GS(foo)

} //namespace mirror
} //namespace boost

BOOST_MPL_ASSERT((
	reflects_type< BOOST_MIRRORED_TYPEDEF_GS(foo) >
));
BOOST_MPL_ASSERT((
	reflects_namespace< BOOST_MIRRORED_TYPEDEF_GS(foo)::scope >
));
BOOST_MPL_ASSERT((
	reflects_global_scope< BOOST_MIRRORED_TYPEDEF_GS(foo)::scope >
));
