using namespace ::boost;
using namespace ::boost::mirror;

BOOST_MPL_ASSERT((
	reflects_global_scope < 
		mpl::front < 
			BOOST_MIRRORED_NAMESPACE(::std)::ancestors 
		>
	>
));

BOOST_MPL_ASSERT((
	reflects_global_scope < 
		mpl::front < 
			BOOST_MIRRORED_NAMESPACE(::boost::mirror)::ancestors
		>
	>
));

BOOST_MPL_ASSERT((
	is_same <
		mpl::back < 
			BOOST_MIRRORED_NAMESPACE(::boost::mirror)::ancestors 
		>,
		BOOST_MIRRORED_NAMESPACE(::boost::mirror)::scope 
	>
));
