section.programlisting:
std::list< data_entry > data;
data_entry elem;
while( in )
{
   in >> boost::io::format( elem ).format( "", "\n" );
   if( in ) data.push_pack( elem );
}

std::list< data_entry > data;
in >> boost::io::format
      (
         data,
         boost::io::containerfmt( boost::io::pairfmt().format( "", "\n" ))
      ).format( "", "[eod]" );
