section.programlisting:
class object
{
   int val;
   public:
      property int Val
      {
         int get() const{ return this -> val; }
         void set( int v ){ this -> val = v; }
      }
};

object ob;
ob.Val = 5; // calls Val.set( 5 );
assert( ob.Val == 5 ); // calls Val.get();

class object
{
   int val;
   public:
      int get_val() const{ return val; }
      void set_val( int v ){ val = v; }
      __declspec(property(get=get_val, set=set_val)) Val;
};

date d;
d.set_month(  7 );  // ok
d.set_month( 13 ); // error: thow?

date d;
d.month =  7;  // ok
d.month = 13; // error: thow?

struct object
{
   property std::map< std::string, int > data[];
};
object ob;
ob.data[ 5 ][ "Martin" ] = 5;
   // error: set_data does not take 3 parameters

struct Prop: public boost::property_type< ... >
{
   value_type get() const;
   void set( const value_type & v );

   inline Prop( constructor_type c );
};

section.para:
A value property holds its own data and provides a property interface
to it. This is implemented via the boost::value_property
class, for example:

section.programlisting:
int i;
scalar_property< aliased_property< int > > ival( i );
ival = 2;
assert( i == 2 );

class object: public boost::noncopyable
{
   int val;
   public:
      scalar_property< aliased_property< int > > Val;
      object(): Val( val ){}
};

class object: public boost::noncopyable
{
   int val;
   public:
      int get_Val() const{ return val; }
      void set_Val( const int & v ){ val = v; }
      scalar_property< object_property
      <
         int, object,
         &object::get_Val, &object::set_Val
      > > Val;
      object(): Val( this ){}
};

class Integer
{
   int val;
   public:
      int get_Val() const{ return val; }
      void set_Val( const int & v ){ val = v; }
      scalar_property< object_property
      <
         int, object,
         &object::get_Val, &object::set_Val
      > > Val;

      const Integer & operator=( const Integer & i )
      {
         val = i.val;
         return *this;
      }
      Integer( const Integer & i ):
         val( i.val ), Val( this )
      {
      }
      Integer( int i ): val( i ), Val( this ){}
};
